import { useState, useEffect } from 'react';

import '../styles/main.css';

import Quote from '../components/Quote';
import Author from '../components/Author';

const Main = () => {
  const [quote, setQuote] = useState('');
  const [author, setAuthor] = useState('');
  const [genre, setGenre] = useState('');
  const [data, setData] = useState(null);

  const getRandomQuote = () => {

    if (!data) {
      return <p>Loading...</p>
    }
    let randomNumber = Math.floor(Math.random() * data.length);
    for (let i = 0; i < data.length; i++) {
      if (i === randomNumber) {
        setQuote(data[i].quoteText);
        setAuthor(data[i].quoteAuthor);
        setGenre(data[i].quoteGenre);
      }
    }
  }

  const getSameAuthor = (data, pickedAuthor) => {
    data.map((quote) => {
      if (quote.quoteAuthor === pickedAuthor) {
        <Quote quote={quote.quoteText} />
      }
    })
  }

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await fetch('https://quote-garden.onrender.com/api/v3/quotes');
        const responseJson = await response.json();
        setData(responseJson.data);
      } catch (err) {
        console.log(err);
      }
    }
    fetchData();
    getRandomQuote();
  }, []);


  const handleMouseClick = (pickedAuthor) => {
    console.log(pickedAuthor);
    getSameAuthor(data, pickedAuthor);
  }


  return (
    <div className='main'>
      <div className='random-button'>
        <button id="random" onClick={getRandomQuote}>random <span className="material-symbols-outlined">
          autorenew
        </span></button>

      </div>
      <div className='quote-components'>
        <Quote quote={quote} />
        <Author
          author={author}
          genre={genre}
          handleMouseClick={handleMouseClick}
        />

      </div>
    </div>
  );
}

export default Main;
