import { useState, useEffect } from 'react';

import '../styles/main.css';

import Quote from '../components/Quote';
import Author from '../components/Author';

const Main = () => {
  const [data, setData] = useState(null);
  const [quote, setQuote] = useState('');
  const [author, setAuthor] = useState('');
  const [genre, setGenre] = useState('');


  useEffect(() => {
    async function fetchData() {
      try {
        const response = await fetch('https://quote-garden.onrender.com/api/v3/quotes');
        const responseJson = await response.json();
        setData(responseJson.data);
      } catch (err) {
        console.log(err);
      }
    }
    fetchData();
  }, []);

  useEffect(() => {
    if (data) {
      getRandomQuote();
    }
  }, []);

  const getRandomQuote = () => {
    if (!data) {
      return <p>Loading...</p>
    }
    let randomNumber = Math.floor(Math.random() * data.length);
    for (let i = 0; i < data.length; i++) {
      if (i === randomNumber) {
        setQuote(data[i].quoteText);
        setAuthor(data[i].quoteAuthor);
        setGenre(data[i].quoteGenre);
      }
    }
  }
  return (
    <div className='main'>
      <div className='random-button'>
        <button id="random" onClick={getRandomQuote}>random</button>
      </div>
      <div className='quote-components'>
        <Quote quote={quote} />
        <Author author={author} genre={genre} />
      </div>
    </div>
  );
}

export default Main;
