import { useState, useEffect } from 'react';

import '../styles/main.css';

import Quote from './Quote';
import Author from './Author';

const Main = () => {
    const [data, setData] = useState(null);
    const [quote, setQuote] = useState('');
    const [author, setAuthor] = useState('');
    const [genre, setGenre] = useState('');

    useEffect (() => {
      async function fetchData() {
        try {
          const response = await fetch('https://quote-garden.onrender.com/api/v3/quotes');
          const data = await response.json();
          console.log(data);
          setData(data);
        } catch (err) {
          console.log(err);
        }
      }

      fetchData();

    }, []);

    const getRandomQuote = (data) => {
      let randomNumber = Math.floor(Math.random() * data.length);
      for (let i=0; i<data.length; i++) {
        if(data.key === randomNumber) {
          setAuthor(data.key.quoteAuthor);
          setQuote(data.key.quoteText);
          setGenre(data.key.quoteGenre)
        }
      }
    }



    return ( 
        <div>
          <button id="random" onClick={getRandomQuote}>random</button>
          <Quote quote={quote} />
          <Author author={author} genre={genre} />
        </div>
     );
}
 
export default Main;
